import React from 'react';
import { Link } from 'react-router-dom';
import heroImg from '../../assets/freelance-hero.jpg'; // adjust the path

export default function Home() {
  return (
    <div className="text-center">
      <img src={heroImg} alt="Hero" className="img-fluid w-100" style={{ maxHeight: '80vh', objectFit: 'cover' }} />
      <h1 className="display-4 mt-4">Hire Top Freelancers</h1>
      <p>Post your project and get work done efficiently.</p>
      <Link to="/register" className="btn btn-primary">Get Started</Link>
    </div>
  );
}
