import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../../api';
import './FreelancerProfile.css'; // Create this for styles

export default function FreelancerProfile() {
  const { id } = useParams();
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await api.get(`/api/projects/freelancer/${id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setData(res.data);
      } catch (err) {
        console.error(err);
        setError('❌ Failed to load freelancer profile');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [id]);

  if (loading) return <p className="text-center mt-5">⏳ Loading profile...</p>;
  if (error) return <p className="text-danger text-center mt-5">{error}</p>;
  if (!data?.freelancer) return <p className="text-center text-muted mt-5">No profile found.</p>;

  const { freelancer, averageRating, reviews } = data;

  return (
    <div className="container py-5">
      <div className="card p-4 shadow-sm">
        <h2 className="fw-bold">{freelancer.name}</h2>
        <p className="text-muted mb-1"><i className="fas fa-envelope me-2"></i>{freelancer.email}</p>
        <p className="mb-3">
          <strong>Average Rating:</strong>{' '}
          {averageRating ? (
            <>
              <span className="text-warning">{averageRating.toFixed(1)} ★</span>
            </>
          ) : (
            'Not rated yet'
          )}
        </p>

        <h4 className="mt-4 mb-3">Client Reviews</h4>
        {reviews.length > 0 ? (
          reviews.map((rev, i) => (
            <div key={i} className="border rounded p-3 mb-3 bg-light">
              <h6 className="fw-bold">{rev.title}</h6>
              <p className="mb-1"><strong>Feedback:</strong> {rev.feedback.message}</p>
              <p className="mb-0">
                Rating:{' '}
                <span className="text-warning">
                  {'★'.repeat(rev.feedback.rating)}
                  {'☆'.repeat(5 - rev.feedback.rating)}
                </span>
              </p>
            </div>
          ))
        ) : (
          <p className="text-muted">No reviews yet.</p>
        )}
      </div>
    </div>
  );
}
