import React, { useEffect, useState } from 'react';
import api from '../../api';

export default function ProjectList() {
  const [projects, setProjects] = useState([]);
  const [user, setUser] = useState(null);
  const [submissions, setSubmissions] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await api.get('/projects/all', {
          headers: { Authorization: token },
        });
        setProjects(res.data);

        const userRes = await api.get('/auth/me', {
          headers: { Authorization: token },
        });
        setUser(userRes.data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchData();
  }, []);

  const handleSubmitWork = async (e, projectId) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    try {
      await api.post(
        `/projects/${projectId}/submit`,
        { submission: submissions[projectId] },
        { headers: { Authorization: token } }
      );
      alert('Work submitted successfully!');
      setSubmissions((prev) => ({ ...prev, [projectId]: '' }));
    } catch (err) {
      alert(err.response?.data?.msg || 'Submission failed');
    }
  };

  return (
    <div className="container my-4">
      <h2 className="mb-4">Projects</h2>
      <div className="row">
        {projects.map((p) => (
          <div className="col-md-6 mb-4" key={p._id}>
            <div className="card shadow-sm h-100">
              <div className="card-body">
                <h5 className="card-title">{p.title}</h5>
                <p className="card-text">{p.description}</p>
                <p><strong>Budget:</strong> ₹{p.budget}</p>

                {user?.role === 'freelancer' && !p.selectedFreelancer && (
                  <form
                    onSubmit={async (e) => {
                      e.preventDefault();
                      const amount = e.target.amount.value;
                      const message = e.target.message.value;
                      const token = localStorage.getItem('token');

                      try {
                        await api.post(`/projects/${p._id}/bid`, { amount, message }, {
                          headers: { Authorization: token },
                        });
                        alert('Bid placed!');
                        e.target.reset();
                      } catch (err) {
                        alert(err.response?.data?.msg || 'Failed to bid');
                      }
                    }}
                  >
                    <input name="amount" type="number" className="form-control mb-2" placeholder="Bid Amount" required />
                    <input name="message" className="form-control mb-2" placeholder="Message" required />
                    <button type="submit" className="btn btn-primary">Place Bid</button>
                  </form>
                )}

                {user?.role === 'freelancer' &&
                  p.selectedFreelancer === user._id &&
                  !p.isSubmitted && (
                    <form onSubmit={(e) => handleSubmitWork(e, p._id)}>
                      <textarea
                        className="form-control mt-3"
                        rows="3"
                        placeholder="Submit your work..."
                        value={submissions[p._id] || ''}
                        onChange={(e) =>
                          setSubmissions((prev) => ({
                            ...prev,
                            [p._id]: e.target.value,
                          }))
                        }
                      />
                      <button type="submit" className="btn btn-success mt-2">Submit Work</button>
                    </form>
                  )}

                {p.isSubmitted && (
                  <div className="alert alert-info mt-3">
                    <strong>Work Submitted:</strong> {p.submission}
                  </div>
                )}

                {user?.role === 'client' && p.isSubmitted && !p.feedback && (
                  <form
                    onSubmit={async (e) => {
                      e.preventDefault();
                      const rating = e.target.rating.value;
                      const message = e.target.message.value;
                      const token = localStorage.getItem('token');

                      try {
                        await api.post(`/projects/${p._id}/feedback`, { rating, message }, {
                          headers: { Authorization: token },
                        });
                        alert('Feedback submitted!');
                      } catch (err) {
                        alert(err.response?.data?.msg || 'Feedback failed');
                      }
                    }}
                  >
                    <textarea name="message" className="form-control my-2" placeholder="Your feedback" required />
                    <select name="rating" className="form-select mb-2" required>
                      <option value="">Rate the freelancer</option>
                      {[1, 2, 3, 4, 5].map(r => (
                        <option key={r} value={r}>{'★'.repeat(r)}</option>
                      ))}
                    </select>
                    <button className="btn btn-warning">Submit Feedback</button>
                  </form>
                )}

                {p.selectedFreelancer && (
                  <p className="mt-3">
                    <strong>Freelancer:</strong>{' '}
                    <a href={`/freelancer/${p.selectedFreelancer}`} className="text-primary">
                      View Profile
                    </a>
                  </p>
                )}

                {p.feedback && (
                  <div className="mt-3">
                    <strong>Client Feedback:</strong>
                    <p>{p.feedback.message}</p>
                    <p>Rating: {'★'.repeat(p.feedback.rating)}{'☆'.repeat(5 - p.feedback.rating)}</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
