import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../../api';

export default function FreelancerProfile() {
  const { id } = useParams(); // freelancer ID from URL
  const [data, setData] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    api.get(`/api/projects/freelancer/${id}`, {
      headers: { Authorization: token }
    })
      .then(res => setData(res.data))
      .catch(err => console.error(err));
  }, [id]);

  if (!data) return <p>Loading...</p>;

  const { freelancer, averageRating, reviews } = data;

  return (
    <div className="container mt-4">
      <h2>{freelancer.name}'s Profile</h2>
      <p><strong>Email:</strong> {freelancer.email}</p>
      <p><strong>Average Rating:</strong> {averageRating || 'Not rated yet'}</p>

      <h4 className="mt-4">Client Reviews</h4>
      {reviews.length > 0 ? (
        reviews.map((rev, i) => (
          <div key={i} className="border rounded p-3 mb-2">
            <h5>{rev.title}</h5>
            <p><strong>Feedback:</strong> {rev.feedback.message}</p>
            <p>Rating: {'★'.repeat(rev.feedback.rating)}{'☆'.repeat(5 - rev.feedback.rating)}</p>
          </div>
        ))
      ) : (
        <p>No reviews yet.</p>
      )}
    </div>
  );
}
