import React, { useEffect, useState } from 'react';
import api from '../../api';

export default function MyProjects() {
    const [projects, setProjects] = useState([]);
    const token = localStorage.getItem('token');

    useEffect(() => {
        api.get('/projects/my', {
            headers: { Authorization: token }
        }).then(res => setProjects(res.data));
    }, []);

    const selectFreelancer = async (projectId, freelancerId) => {
        try {
            await api.post(`/projects/${projectId}/select`, { freelancerId }, {
                headers: { Authorization: token }
            });
            alert('Freelancer selected!');
        } catch (err) {
            alert(err.response?.data?.msg || 'Error selecting');
        }
    };

    return (
        <div>
            <h2>My Posted Projects</h2>
            {projects.map(p => (
                <div key={p._id} style={{ border: '1px solid black', margin: 10, padding: 10 }}>
                    <h3>{p.title}</h3>
                    <p>{p.description}</p>
                    <h4>Bids:</h4>
                    {p.bids.length === 0 && <p>No bids yet.</p>}
                    {p.bids.map(bid => (
                        <div key={bid._id}>
                            <p><strong>{bid.freelancer.name}</strong> - â‚¹{bid.amount}</p>
                            <p>{bid.message}</p>
                            {!p.selectedFreelancer && (
                                <button onClick={() => selectFreelancer(p._id, bid.freelancer._id)}>
                                    Select this Freelancer
                                </button>
                            )}
                        </div>
                    ))}
                    {p.submission && !p.feedback && (
                        <form onSubmit={(e) => {
                            e.preventDefault();
                            const message = e.target.message.value;
                            const rating = e.target.rating.value;
                            const token = localStorage.getItem('token');

                            api.post(`/projects/${p._id}/feedback`, { message, rating }, {
                                headers: { Authorization: token }
                            })
                                .then(() => alert('Feedback submitted'))
                                .catch(err => alert(err.response?.data?.msg || 'Error submitting feedback'));
                        }}>
                            <textarea name="message" placeholder="Feedback message" required className="form-control my-2" />
                            <select name="rating" required className="form-control my-2">
                                <option value="">Select Rating</option>
                                {[1, 2, 3, 4, 5].map(r => (
                                    <option key={r} value={r}>{r} Star</option>
                                ))}
                            </select>
                            <button className="btn btn-primary">Submit Feedback</button>
                        </form>
                    )}

                </div>
            ))}
        </div>
    );
}
