import React, { useEffect, useState } from 'react';
import api from '../../api';

export default function MyProjects() {
  const [projects, setProjects] = useState([]);
  const token = localStorage.getItem('token');

  const fetchProjects = async () => {
    if (!token) return;
    try {
      const res = await api.get('/api/projects/my', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProjects(res.data);
    } catch (err) {
      console.error('Failed to fetch projects:', err);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  const selectFreelancer = async (projectId, freelancerId) => {
    try {
      await api.post(`/projects/${projectId}/select`, { freelancerId }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Freelancer selected!');
      fetchProjects(); // Refresh after selection
    } catch (err) {
      alert(err.response?.data?.msg || 'Error selecting freelancer');
    }
  };

  const handleFeedbackSubmit = async (e, projectId) => {
    e.preventDefault();
    const { message, rating } = e.target;

    try {
      await api.post(`/projects/${projectId}/feedback`, {
        message: message.value,
        rating: parseInt(rating.value),
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Feedback submitted successfully');
      fetchProjects(); // Refresh after feedback
    } catch (err) {
      alert(err.response?.data?.msg || 'Error submitting feedback');
    }
  };

  return (
    <div className="container mt-4">
      <h2>My Posted Projects</h2>
      {projects.map((p) => (
        <div key={p._id} className="card my-4 p-3 shadow-sm">
          <h4>{p.title}</h4>
          <p>{p.description}</p>

          <h5 className="mt-3">Bids</h5>
          {p.bids.length === 0 ? (
            <p className="text-muted">No bids yet.</p>
          ) : (
            p.bids.map((bid) => (
              <div key={bid._id} className="mb-2 border-bottom pb-2">
                <strong>{bid.freelancer?.name}</strong> – ₹{bid.amount}
                <p className="mb-1">{bid.message}</p>
                {!p.selectedFreelancer && (
                  <button
                    className="btn btn-sm btn-success"
                    onClick={() => selectFreelancer(p._id, bid.freelancer._id)}
                  >
                    Select this Freelancer
                  </button>
                )}
              </div>
            ))
          )}

          {/* Work submission view */}
          {p.isSubmitted && (
            <div className="alert alert-info mt-3">
              <strong>Work Submitted:</strong> {p.submission}
            </div>
          )}

          {/* Feedback form */}
          {p.isSubmitted && !p.feedback && (
            <form onSubmit={(e) => handleFeedbackSubmit(e, p._id)} className="mt-3">
              <textarea
                name="message"
                className="form-control my-2"
                placeholder="Write feedback message"
                required
              />
              <select name="rating" className="form-select my-2" required>
                <option value="">Select Rating</option>
                {[1, 2, 3, 4, 5].map((r) => (
                  <option key={r} value={r}>{r} Star</option>
                ))}
              </select>
              <button className="btn btn-primary">Submit Feedback</button>
            </form>
          )}

          {/* Display feedback */}
          {p.feedback && (
            <div className="mt-3">
              <strong>Feedback Given:</strong>
              <p>{p.feedback.message}</p>
              <p>Rating: {'★'.repeat(p.feedback.rating)}{'☆'.repeat(5 - p.feedback.rating)}</p>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
