import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../api';

export default function Register() {
  const [form, setForm] = useState({ name: '', email: '', password: '', role: 'freelancer' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await api.post('/auth/register', form);
      navigate('/login');
    } catch (err) {
      setError(err.response?.data?.msg || 'Registration failed');
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
      <div className="card p-4 shadow" style={{ width: '100%', maxWidth: 400 }}>
        <h3 className="text-center mb-4">Register</h3>
        {error && <div className="alert alert-danger">{error}</div>}
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label>Name</label>
            <input name="name" value={form.name} onChange={handleChange} required className="form-control" />
          </div>
          <div className="mb-3">
            <label>Email</label>
            <input name="email" type="email" value={form.email} onChange={handleChange} required className="form-control" />
          </div>
          <div className="mb-3">
            <label>Password</label>
            <input name="password" type="password" value={form.password} onChange={handleChange} required className="form-control" />
          </div>
          <div className="mb-3">
            <label>Role</label>
            <select name="role" value={form.role} onChange={handleChange} className="form-select">
              <option value="freelancer">Freelancer</option>
              <option value="client">Client</option>
            </select>
          </div>
          <button className="btn btn-primary w-100">Register</button>
        </form>
      </div>
    </div>
  );
}
