import React, { useEffect, useState } from 'react';
import api from '../../api';

export default function AdminPanel() {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return setError('No token found');

    api.get('/api/admin/users', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then((res) => {
        console.log('Users loaded:', res.data);
        setUsers(res.data);
      })
      .catch((err) => {
        console.error('Admin fetch error:', err.response?.data || err.message);
        setError(err.response?.data?.msg || 'Unauthorized');
      });
  }, []);

  const toggleBlock = async (id) => {
    const token = localStorage.getItem('token');
    try {
      await api.patch(`/api/admin/users/${id}/toggle`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Toggle user status in state
      setUsers((prevUsers) =>
        prevUsers.map((u) =>
          u._id === id ? { ...u, isBlocked: !u.isBlocked } : u
        )
      );
    } catch (err) {
      alert(err.response?.data?.msg || 'Error toggling user');
    }
  };

  return (
    <div className="container mt-4">
      <h2>Admin Panel</h2>
      {error && <p className="text-danger">{error}</p>}

      {users.length === 0 && !error ? (
        <p>Loading or no users found.</p>
      ) : (
        <table className="table table-bordered mt-3">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Role</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {users.map((u) => (
              <tr key={u._id}>
                <td>{u.name}</td>
                <td>{u.email}</td>
                <td>{u.role}</td>
                <td>{u.isBlocked ? 'Blocked' : 'Active'}</td>
                <td>
                  <button
                    className={`btn btn-sm ${u.isBlocked ? 'btn-success' : 'btn-danger'}`}
                    onClick={() => toggleBlock(u._id)}
                  >
                    {u.isBlocked ? 'Unblock' : 'Block'}
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
