import React, { useEffect, useState } from 'react';
import api from '../../api';
import { useNavigate } from 'react-router-dom';

export default function AdminPanel() {
    const [users, setUsers] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    const token = localStorage.getItem('token');

    useEffect(() => {
        if (!token) {
            navigate('/login');
            return;
        }

        api.get('/api/admin/users', {
            headers: { Authorization: `Bearer ${token}` }
        })
            .then(res => {
                console.log('Admin users:', res.data);
                setUsers(res.data);
                setLoading(false);
            })
            .catch(err => {
                console.error('Error:', err.response?.data || err.message);
                if (err.response?.status === 401) {
                    navigate('/login');
                } else {
                    setError(err.response?.data?.msg || 'Unauthorized');
                }
                setLoading(false);
            });
    }, [token, navigate]);

    const toggleBlock = async (id) => {
        try {
            await api.patch(`/api/admin/users/${id}/toggle`, {}, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setUsers(users.map(u =>
                u._id === id ? { ...u, isBlocked: !u.isBlocked } : u
            ));
        } catch (err) {
            alert('Error toggling user status');
        }
    };

    return (
        <div className="container mt-4">
            <h2>Admin Panel</h2>

            {error && <p className="text-danger">{error}</p>}

            {loading ? (
                <p>Loading...</p>
            ) : users.length === 0 ? (
                <p>No users found.</p>
            ) : (
                <table className="table table-bordered mt-3">
                    <thead>
                        <tr>
                            <th>Name</th><th>Email</th><th>Role</th><th>Status</th><th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(u => (
                            <tr key={u._id}>
                                <td>{u.name}</td>
                                <td>{u.email}</td>
                                <td>{u.role}</td>
                                <td>{u.isBlocked ? 'Blocked' : 'Active'}</td>
                                <td>
                                    <button
                                        onClick={() => toggleBlock(u._id)}
                                        className={`btn btn-sm ${u.isBlocked ? 'btn-success' : 'btn-danger'}`}
                                    >
                                        {u.isBlocked ? 'Unblock' : 'Block'}
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}
