// src/components/layout/Navbar.jsx
import React from 'react';
import { Link } from 'react-router-dom';

export default function Navbar({ user }) {
  const token = localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark px-4">
      <Link className="navbar-brand" to="/">SB Works</Link>

      <div className="collapse navbar-collapse">
        <ul className="navbar-nav ms-auto">
          {!token && (
            <>
              <li className="nav-item"><Link to="/login" className="nav-link">Login</Link></li>
              <li className="nav-item"><Link to="/register" className="nav-link">Register</Link></li>
            </>
          )}

          {user?.role === 'client' && (
            <>
              <li className="nav-item"><Link to="/create-project" className="nav-link">Post Project</Link></li>
              <li className="nav-item"><Link to="/my-projects" className="nav-link">My Projects</Link></li>
            </>
          )}

          {user?.role === 'freelancer' && (
            <li className="nav-item"><Link to="/projects" className="nav-link">Find Projects</Link></li>
          )}

          {user?.role === 'admin' && (
            <li className="nav-item"><Link to="/admin" className="nav-link">Admin</Link></li>
          )}

          {token && (
            <li className="nav-item">
              <button onClick={handleLogout} className="btn btn-sm btn-outline-light ms-2">Logout</button>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
}
