import React, { useEffect, useState } from 'react';
import api from '../../api';

export default function ProjectList() {
  const [projects, setProjects] = useState([]);
  const [user, setUser] = useState(null);
  const [submissions, setSubmissions] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const headers = { Authorization: `Bearer ${token}` };

        const [projectRes, userRes] = await Promise.all([
          api.get('/api/projects/all', { headers }),
          api.get('/api/auth/me', { headers }),
        ]);

        setProjects(projectRes.data);
        setUser(userRes.data);
      } catch (err) {
        console.error('Error loading data:', err);
      }
    };

    fetchData();
  }, []);

  const handleSubmitWork = async (e, projectId) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      await api.post(
        `/api/projects/${projectId}/submit`,
        { submission: submissions[projectId] },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('✅ Work submitted successfully!');
      setSubmissions(prev => ({ ...prev, [projectId]: '' }));

      const updated = await api.get('/api/projects/all', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProjects(updated.data);
    } catch (err) {
      alert(err.response?.data?.msg || '❌ Submission failed');
    }
  };

  const handleBid = async (e, p) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const { amount, message } = e.target;

    try {
      await api.post(`/api/projects/${p._id}/bid`, {
        amount: amount.value,
        message: message.value,
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('✅ Bid placed!');
      e.target.reset();
    } catch (err) {
      alert(err.response?.data?.msg || '❌ Failed to place bid');
    }
  };

  const handleFeedback = async (e, p) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const { message, rating } = e.target;

    try {
      await api.post(`/api/projects/${p._id}/feedback`, {
        message: message.value,
        rating: parseInt(rating.value),
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('✅ Feedback submitted!');
      const updated = await api.get('/api/projects/all', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProjects(updated.data);
    } catch (err) {
      alert(err.response?.data?.msg || '❌ Feedback failed');
    }
  };

  return (
    <div className="container my-4">
      <h2 className="mb-4">All Projects</h2>
      <div className="row">
        {projects.map((p) => (
          <div className="col-md-6 mb-4" key={p._id}>
            <div className="card shadow-sm h-100">
              <div className="card-body">
                <h5 className="card-title">{p.title}</h5>
                <p>{p.description}</p>
                <p><strong>Budget:</strong> ₹{p.budget}</p>

                {/* Place Bid */}
                {user?.role === 'freelancer' && !p.selectedFreelancer && (
                  <form onSubmit={(e) => handleBid(e, p)} className="mt-3">
                    <input name="amount" type="number" className="form-control mb-2" placeholder="Bid Amount" required />
                    <input name="message" className="form-control mb-2" placeholder="Message" required />
                    <button type="submit" className="btn btn-sm btn-primary">Place Bid</button>
                  </form>
                )}

                {/* Submit Work */}
                {user?.role === 'freelancer' &&
                  p.selectedFreelancer === user._id &&
                  !p.isSubmitted && (
                    <form onSubmit={(e) => handleSubmitWork(e, p._id)} className="mt-3">
                      <textarea
                        className="form-control"
                        rows="3"
                        placeholder="Submit your work..."
                        value={submissions[p._id] || ''}
                        onChange={(e) =>
                          setSubmissions((prev) => ({
                            ...prev,
                            [p._id]: e.target.value,
                          }))
                        }
                      />
                      <button type="submit" className="btn btn-success mt-2">Submit Work</button>
                    </form>
                  )}

                {/* Show Submitted Work */}
                {p.isSubmitted && (
                  <div className="alert alert-info mt-3">
                    <strong>Work Submitted:</strong> {p.submission}
                  </div>
                )}

                {/* Feedback form for client */}
                {user?.role === 'client' && p.isSubmitted && !p.feedback && (
                  <form onSubmit={(e) => handleFeedback(e, p)} className="mt-3">
                    <textarea name="message" className="form-control mb-2" placeholder="Your feedback" required />
                    <select name="rating" className="form-select mb-2" required>
                      <option value="">Rate the freelancer</option>
                      {[1, 2, 3, 4, 5].map(r => (
                        <option key={r} value={r}>{'★'.repeat(r)}</option>
                      ))}
                    </select>
                    <button className="btn btn-warning">Submit Feedback</button>
                  </form>
                )}

                {/* Freelancer name */}
                {p.selectedFreelancer && (
                  <p className="mt-3">
                    <strong>Freelancer:</strong>{' '}
                    <a href={`/freelancer/${p.selectedFreelancer}`} className="text-primary">
                      View Profile
                    </a>
                  </p>
                )}

                {/* Show Feedback */}
                {p.feedback && (
                  <div className="mt-3">
                    <strong>Client Feedback:</strong>
                    <p>{p.feedback.message}</p>
                    <p>Rating: {'★'.repeat(p.feedback.rating)}{'☆'.repeat(5 - p.feedback.rating)}</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
